import React from 'react';

class Main extends React.Component{

    state = {
        films: [],
        title: '',
        imgUrl: '',
        year: +'',
        type: '',
        loading: true
    }

    componentDidMount() {
      fetch("http://www.omdbapi.com/?apikey=b804fd78&s=matrix&page=1")
      .then((response) => response.json())
      .then((data) => this.setState({films: data, loading: false}))
    };



    render() {
        return(
            <main classNameNameName="container content">
                <div className="row">   
                    <div className="col s12 m4">
                        <div className="card">
                            <div className="card-image">
                            <img src="https://www.purinaone.ru/dog/sites/default/files/2021-11/shutterstock_1155759124_OG_1.jpg"/>
                            <span className="card-title"></span>
                            </div>
                            <div className="card-content">
                            <p>I am a very simple card. I am good at containing small bits of information.
                            I am convenient because I require little markup to use effectively.</p>
                            </div>
                            <div className="card-action">
                            <a href="#">This is a link</a>
                            </div>
                        </div>
                    </div>

                </div>
            </main>
        )
    }
}

export default Main;
